
CREATE TABLE IF NOT EXISTS `#__jdocmanual_menu` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `language_code` CHAR(8) NOT NULL DEFAULT 'en',
  `source_id` int(11) NOT NULL DEFAULT '1',
  `menu` MEDIUMTEXT NULL DEFAULT NULL,
  `state` TINYINT NOT NULL DEFAULT '1',
  `last_update` DATETIME on update CURRENT_TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`)
) ENGINE = InnoDB;

CREATE TABLE IF NOT EXISTS `#__jdocmanual_pages` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `language_code` CHAR(8) NOT NULL DEFAULT 'en',
  `jdoc_key` varchar(1024) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `content` MEDIUMTEXT COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `gfm` MEDIUMTEXT COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `content_gfm` MEDIUMTEXT COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `state` TINYINT(4) NOT NULL DEFAULT '1',
  `last_update` timestamp on update CURRENT_TIMESTAMP default CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`),
  INDEX (`language_code`)
) ENGINE = InnoDB;

CREATE TABLE IF NOT EXISTS `#__jdocmanual_gfmindex` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `jdoc_key` varchar(180) NOT NULL,
  `manual` varchar(128) NOT NULL,
  `language` char(7) NOT NULL,
  `heading` varchar(256) NOT NULL,
  `filename` VARCHAR(256) NOT NULL,
  `display_title` VARCHAR(512) NOT NULL,
  `state` TINYINT(4) NOT NULL DEFAULT '0',
  `created` datetime DEFAULT NULL,
  `created_by` int(10) UNSIGNED NOT NULL DEFAULT '0',
  `created_by_alias` varchar(255) NOT NULL DEFAULT '',
  `modified` datetime DEFAULT NULL,
  `modified_by` int(10) UNSIGNED NOT NULL DEFAULT '0',
  `html` MEDIUMTEXT NULL,
  PRIMARY KEY (`id`),
  INDEX (`state`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

ALTER TABLE `#__jdocmanual_gfmindex`
  ADD UNIQUE KEY `language` (`jdoc_key`, `manual`, `language`);

CREATE TABLE IF NOT EXISTS `#__jdocmanual_stashes` (
  `id` INT NOT NULL AUTO_INCREMENT ,
  `user_id` INT NOT NULL ,
  `page_id` INT NOT NULL ,
  `jdoc_key` varchar(180) NOT NULL,
  `manual` varchar(128) NOT NULL,
  `language` char(7) NOT NULL,
  `heading` varchar(256) NOT NULL,
  `filename` VARCHAR(256) NOT NULL,
  `display_title` VARCHAR(512) NOT NULL,
  `pr` INT NOT NULL DEFAULT '0',
  `created` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `modified` TIMESTAMP on update CURRENT_TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `commit_message` VARCHAR(256) NULL,
  `comments` TEXT NULL,
  `markdown_text` MEDIUMTEXT NOT NULL ,
  PRIMARY KEY (`id`),
  INDEX (`user_id`),
  INDEX (`page_id`)) ENGINE = InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

CREATE TABLE IF NOT EXISTS `#__jdocmanual_sources` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `title` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `index_url` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `page_url` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `lft` int(11) NOT NULL DEFAULT '0',
  `rgt` int(11) NOT NULL DEFAULT '0',
  `state` tinyint(3) NOT NULL DEFAULT '1',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

CREATE TABLE IF NOT EXISTS `#__jdocmanual_languages` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `code` varchar(8) COLLATE utf8mb4_unicode_ci NOT NULL,
  `title` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `index_language` INT NOT NULL DEFAULT '0',
  `page_language` INT NOT NULL DEFAULT '0',
  `lft` int(11) NOT NULL DEFAULT '0',
  `rgt` int(11) NOT NULL DEFAULT '0',
  `state` tinyint(3) NOT NULL DEFAULT '1',
  PRIMARY KEY (`id`),
  UNIQUE KEY `code` (`code`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

